sp_widget.client_script:
function($scope, $http, $window, spUtil, $log, spAriaUtil) {
	var c = this;
	c.message = '';
	c.genderchoicelistoption = {
		hideSearch: true,
		placeholder: "Gender"
	};
	c.genderchoice = [{display:"Male",value: "Male"}, 
										{display:"Female",value: "Female"}];
	c.triggerSubmit = function() {
		c.submitInProgress = true;
		var aMandatory = ['userfirst', 'userlast', 'useremail', 'usergender', 'useryear'];
		for(var i = 0; i < aMandatory.length; i++){
			if (!c.data[aMandatory[i]]) {
				c.data.message = 'Please fill out all the fields!';
				c.submitInProgress = false;
			} // if field
		} // each field
		var reEmail = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
		if (!reEmail.test( c.data.useremail.toLowerCase())) {
			c.data.message = 'Please fill out a valid email!';
			c.submitInProgress = false;
		} // if email
		var iMinYear=new Date().getFullYear() - 5;
		var iMaxYear=new Date().getFullYear() - 85;
		var iYear = parseInt(c.data.useryear);
		if ((isNaN(c.data.useryear)) || (iYear > iMinYear) || (iYear < iMaxYear)) {
			c.data.message = 'Please fill out a valid year!';
			c.submitInProgress = false;
		} // if year
		if (c.submitInProgress) {
			c.server.update().then(function(response) {
				if (response.success == 'Success') {
					$window.location.href = '?id=btp_overview';
				} else {
					c.submitInProgress = false;
					c.data.message = response.message;
				} // if message
			});
		}
	}
}

sp_widget.script:
(function() {
	if (!input) {
		gs.info('Initiating Account action: ' + options.action);
		data.bLoggedIn = gs.getSession().isLoggedIn();
		data.userfirst = '';
		data.userlast = '';
		data.useremail = '';
		data.usergender = '';
		data.useryear = '';
		data.title = 'Account';
		data.success = '';
		data.message = '';
		if (options.action == 'create') {
			data.title = 'Create Account';
		} else if (data.bLoggedIn == false) {
			data.redirect("\bck?id=btp_login");
		} else {
			data.title = 'Edit Account';
			var grParticipant = new GlideRecord('x_144441_badminton_player');
			grParticipant.addQuery('user', gs.getUserID());
			grParticipant.query();
			if (grParticipant.next()) {
				data.userfirst = grParticipant.user.first_name.getDisplayValue();
				data.userlast = grParticipant.user.last_name.getDisplayValue();
				data.useremail = grParticipant.user.email.getDisplayValue();
				data.usergender = grParticipant.user.gender.getDisplayValue();
				data.useryear = grParticipant.getValue('year');
			} else {
				data.redirect("\bck?id=btp_overview");
			} // if user
		} // if action

	} else {
		var bInsert = true;
		var grUser = new GlideRecord('sys_user');
		var grParticipant = new GlideRecord('x_144441_badminton_player');
		grUser.addQuery('user_name', input.useremail);
		grUser.query();
		if (grUser.next()) {
			if (options.action == 'create') {
				data.message = 'User already exists, cannot insert!';
				return;
			} // if create
			grParticipant.addQuery('user', grUser.getUniqueValue());
			grParticipant.query();
			grParticipant.next();
			bInsert = false;
		} // if new user
		grUser.setValue('user_name', input.useremail);
		grUser.setValue('first_name', input.userfirst);
		grUser.setValue('last_name', input.userlast);
		grUser.setValue('email', input.useremail);
		grUser.setValue('gender', input.usergender);
		grParticipant.setValue('name', input.userfirst + ' ' + input.userlast + ' (' + input.useryear + ')');
		grParticipant.setValue('year', input.useryear);
		if (bInsert) {
			grUser.insert();
			grParticipant.setValue('user', grUser.getUniqueValue());
			grParticipant.insert();
		} else {
			grUser.update();
			grParticipant.update();
		} // if insert
		data.success = 'Success';
	} // if input
})();
